-- Detail Totals
SELECT NULL AS 'DEPOSIT SLIP', NULL AS 'SESSION ID', NULL AS 'CASHIER USERID', NULL AS 'PAYMENT ID', NULL AS 'RECORD ID', NULL AS 'PAYOR', NULL AS 'DATE PAYMENT', NULL AS 'PAYMENT METHOD', SUM([AMOUNT]) AS 'AMOUNT', NULL AS 'PAYMENT REFERENCE', NULL AS 'PAYMENT COMMENTS'
FROM (
