FROM [DBO].[V_FEE_PAYMENT_HISTORY] WITH(NOLOCK) 
INNER JOIN [DBO].[V_CASHIER_SESSION] WITH(NOLOCK)  ON [DBO].[V_CASHIER_SESSION].[SESSION_ID]=[DBO].[V_FEE_PAYMENT_HISTORY].[SESSION_ID]
WHERE (([DBO].[V_FEE_PAYMENT_HISTORY].[UPDATED_DATE] BETWEEN '2016-03-01T00:00:00.000' AND '2018-04-30T23:59:59.000')) AND (([DBO].[V_FEE_PAYMENT_HISTORY].[AGENCY_ID] = 'CALCANNABIS')) AND (([DBO].[V_CASHIER_SESSION].[AGENCY_ID] = 'CALCANNABIS'))
ORDER BY [DBO].[V_FEE_PAYMENT_HISTORY].[ACTION] ASC, [DBO].[V_CASHIER_SESSION].[DEPOSIT_SLIP] ASC, [DBO].[V_FEE_PAYMENT_HISTORY].[DATE_PAYMENT] ASC
) AS q1 
 GROUP BY [__order1]
ORDER BY [__order1];

